let buy=document.querySelectorAll(".popup-item-buy");const modalCart=document.querySelector(".modal-cart"),closerCart=document.querySelector(".close-cart");buy.forEach(e=>{e.addEventListener("click",()=>{modalCart.classList.add("modal-cart-show")})}),closerCart.addEventListener("click",function(e){e.preventDefault(),modalCart.classList.remove("modal-cart-show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&modalCart.classList.contains("modal-cart-show")&&(e.preventDefault(),modalCart.classList.remove("modal-cart-show"))});
