let sliderIndex=1;const buttons=document.querySelectorAll(".service-btn"),items=document.querySelectorAll(".services-promo-item");viewItems(sliderIndex);for(let e=0;e<buttons.length;++e)buttons[e].onclick=function(){for(let e=0;e<buttons.length;++e)buttons[e].classList.remove("current");for(let e=0;e<items.length;++e)items[e].classList.remove("services-promo-current");buttons[e].classList.add("current"),items[e].classList.add("services-promo-current")};function plusItems(e){viewItems(sliderIndex+=e)}function currentSlide(e){viewItems(sliderIndex=e)}function viewItems(e){for(e>items.length&&(sliderIndex=1),e<1&&(sliderIndex=items.length),i=0;i<items.length;++i)items[i].classList.remove("services-promo-current");for(i=0;i<buttons.length;++i)buttons[i].classList.remove("current");items[sliderIndex-1].classList.add("services-promo-current"),buttons[sliderIndex-1].classList.add("current")}
