let slideIndex=2;const btnPrev=document.querySelector(".controls-arrow-left"),btnNext=document.querySelector(".controls-arrow-right"),indicators=document.querySelectorAll(".slider-controls button"),slides=document.querySelectorAll(".slider-item");showSlides(slideIndex),btnPrev.onclick=(()=>{plusSlides(-1)}),btnNext.onclick=(()=>{plusSlides(1)});for(let e=0;e<indicators.length;e++)indicators[e].onclick=function(){for(let e=0;e<indicators.length;e++)indicators[e].classList.remove("current");for(let e=0;e<slides.length;e++)slides[e].classList.remove("slide-current");indicators[e].classList.add("current"),slides[e].classList.add("slide-current")};function plusSlides(e){showSlides(slideIndex+=e)}function currentSlide(e){showSlides(slideIndex=e)}function showSlides(e){e>slides.length&&(slideIndex=1),e<1&&(slideIndex=slides.length);for(let e=0;e<slides.length;e++)slides[e].classList.remove("slide-current");for(let e=0;e<indicators.length;e++)indicators[e].classList.remove("current");slides[slideIndex-1].classList.add("slide-current"),indicators[slideIndex-1].classList.add("current")}
